CREATE TABLE IF NOT EXISTS `pages` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `subject_id` int(11) NOT NULL,  `menu_name` varchar(30) NOT NULL,  `position` int(3) NOT NULL,  `visible` tinyint(1) NOT NULL,  `content` text NOT NULL,  PRIMARY KEY (`id`)) ENGINE=MyISAM  DEFAULT CHARSET=utf8 AUTO_INCREMENT=10 ;INSERT INTO `pages` (`id`, `subject_id`, `menu_name`, `position`, `visible`, `content`) VALUES(1, 1, 'Home', 1, 1, '<p><strong><span style="color: #000080;">Welcome To The&nbsp;<span style="text-decoration: underline;"><em>Home Page</em></span></span></strong><span style="color: #000080;"><em> Home Page!</em></span></p>'),(2, 1, 'page1', 2, 1, 'Page 1.........'),(3, 3, 'Admin Login', 1, 1, '<a href="staff.php">Login to Admin Area</a>');CREATE TABLE IF NOT EXISTS `subjects` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `menu_name` varchar(30) NOT NULL,  `position` int(3) NOT NULL,  `visible` tinyint(1) NOT NULL,  PRIMARY KEY (`id`)) ENGINE=MyISAM  DEFAULT CHARSET=utf8 AUTO_INCREMENT=7 ;INSERT INTO `subjects` (`id`, `menu_name`, `position`, `visible`) VALUES(1, 'Home', 1, 1),(2, 'Page1', 2, 1),(3, 'About Us', 3, 1);CREATE TABLE IF NOT EXISTS `users` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `username` varchar(50) NOT NULL,  `hashed_password` varchar(40) NOT NULL,  PRIMARY KEY (`id`)) ENGINE=MyISAM  DEFAULT CHARSET=utf8 AUTO_INCREMENT=5 ;INSERT INTO `users` (`id`, `username`, `hashed_password`) VALUES(1, 'mike', '0a1b87f9b591e351aebd2a55101747dfc2aa7624');
